import{_ as e,c as r,o as i,ag as t}from"./chunks/framework.BhcQuJV0.js";const f=JSON.parse('{"title":"Uniapp APP 云打包 安卓 和 IOS","description":"","frontmatter":{},"headers":[],"relativePath":"frontend/Uniapp/Uniapp打包IOS和Android.md","filePath":"frontend/Uniapp/Uniapp打包IOS和Android.md","lastUpdated":1750231412000}'),l={name:"frontend/Uniapp/Uniapp打包IOS和Android.md"};function n(o,a,p,d,h,s){return i(),r("div",null,a[0]||(a[0]=[t('<h1 id="uniapp-app-云打包-安卓-和-ios" tabindex="-1">Uniapp APP 云打包 安卓 和 IOS <a class="header-anchor" href="#uniapp-app-云打包-安卓-和-ios" aria-label="Permalink to &quot;Uniapp APP 云打包 安卓 和 IOS&quot;">​</a></h1><h2 id="app-基础信息完善" tabindex="-1">APP 基础信息完善 <a class="header-anchor" href="#app-基础信息完善" aria-label="Permalink to &quot;APP 基础信息完善&quot;">​</a></h2><ol><li>应用名称</li><li>应用描述</li><li>应用版本名称（版本号）</li><li>应用版本号</li><li>应用图标（准备一个 1024*1024 的图标，HBuilder 会自动生成其他尺寸的图标）</li><li>应用启动图配置（安卓就准备一张启动图即可，IOS 的需要自定义 Storyboard 启动界面）</li></ol><h2 id="android-证书生成" tabindex="-1">Android 证书生成 <a class="header-anchor" href="#android-证书生成" aria-label="Permalink to &quot;Android 证书生成&quot;">​</a></h2><p>安卓可以使用云端证书也可以自建证书，推荐使用自建证书</p><p>参考文章：<a href="https://ask.dcloud.net.cn/article/35985" target="_blank" rel="noreferrer">安卓证书使用指南</a></p><h2 id="ios-证书生成" tabindex="-1">IOS 证书生成 <a class="header-anchor" href="#ios-证书生成" aria-label="Permalink to &quot;IOS 证书生成&quot;">​</a></h2><p>前提条件下：需要苹果开发者账号，并且已经创建了 AppID</p><p>IOS 相关证书和描述文件的生成需要借助 Mac 才可以创建上面这些东西，在 Windows 可以使用 <a href="https://www.yunedit.com/" target="_blank" rel="noreferrer">香蕉云编</a> 网站生成相关证书和描述文件</p><ol><li>证书文件（.p12）</li><li>证书私钥密码</li><li>描述文件</li></ol><ul><li>苹果手机的 <code>UDID</code> 标识可以通过爱思助手获取</li><li>IOS 开发描述文件必须绑定调试设备，只有授权的设备才可以直接安装基座，所以在申请开发描述文件之前，先添加调试的 IOS 设备的 UDID</li></ul><h2 id="开发测试" tabindex="-1">开发测试 <a class="header-anchor" href="#开发测试" aria-label="Permalink to &quot;开发测试&quot;">​</a></h2><p>本地开发过程中可以直接运行在 H5 即可，也可以运行在安卓模拟器上，IOS 则需要通过 USB 连接手机，使用自定义基座后运行</p><p>测试可以使用 <a href="https://www.pgyer.com/" target="_blank" rel="noreferrer">蒲公英</a> 分发平台，进行内部分发进行测试使用</p><h2 id="app-云打包" tabindex="-1">APP 云打包 <a class="header-anchor" href="#app-云打包" aria-label="Permalink to &quot;APP 云打包&quot;">​</a></h2><p>直接使用 HBuilder 的云打包选项，配置相关文件和参数即可 生成 APK 和 IPA 文件</p><h2 id="ipa-文件上传" tabindex="-1">IPA 文件上传 <a class="header-anchor" href="#ipa-文件上传" aria-label="Permalink to &quot;IPA 文件上传&quot;">​</a></h2><p>IPA 文件的上传也可以使用 <a href="https://www.yunedit.com/" target="_blank" rel="noreferrer">香蕉云编</a> 来完成</p><p>首先需要创建一个 开发者账号专用密码 用于上传 IPA 文件</p><h2 id="参考文章" tabindex="-1">参考文章 <a class="header-anchor" href="#参考文章" aria-label="Permalink to &quot;参考文章&quot;">​</a></h2><ol><li><a href="https://juejin.cn/post/7216608199012237372" target="_blank" rel="noreferrer">最新uniapp打包IOS详细步骤</a></li><li><a href="https://juejin.cn/post/7264939254290579495" target="_blank" rel="noreferrer">教你做事，uniapp ios App 打包全流程</a></li><li><a href="https://www.bilibili.com/video/BV1Jp4y1V7ad/" target="_blank" rel="noreferrer">uniapp打包上线微信小程序、安卓、IOS流程</a></li><li><a href="https://blog.csdn.net/cengjing123_/article/details/129490094" target="_blank" rel="noreferrer">uniapp运行到IOS真机提示 错误:请查看是否设备未加入到证书列表或者确认证书类型是否匹配</a></li><li><a href="https://blog.csdn.net/weixin_48914851/article/details/114979266" target="_blank" rel="noreferrer">hbuilderx云打包ios证书的生成方法</a></li><li><a href="https://juejin.cn/post/7282972975933964323" target="_blank" rel="noreferrer">uniapp项目实践总结(二十四)安卓平台 APP 打包教程</a></li><li><a href="https://juejin.cn/post/7284221961621028919" target="_blank" rel="noreferrer">uniapp项目实践总结(二十五)苹果 ios 平台 APP 打包教程</a></li><li><a href="https://ask.dcloud.net.cn/article/37835" target="_blank" rel="noreferrer">最新 App store 上架教程（uni-app版）</a></li><li><a href="https://blog.csdn.net/weixin_43742167/article/details/129876427" target="_blank" rel="noreferrer">uniapp开发云打包详细流程（包含安卓Android和苹果IOS）</a></li><li><a href="https://www.pgyer.com/tools/udid" target="_blank" rel="noreferrer">获取苹果手机设备的UDID</a></li></ol>',21)]))}const u=e(l,[["render",n]]);export{f as __pageData,u as default};
