import{_ as o,c as a,a3 as i,o as l}from"./chunks/framework.H5EfqC1G.js";const p=JSON.parse('{"title":"Mac 配置和使用总结","description":"","frontmatter":{},"headers":[],"relativePath":"posts/Mac配置和使用.md","filePath":"posts/Mac配置和使用.md","lastUpdated":1733627094000}'),c={name:"posts/Mac配置和使用.md"};function d(t,e,n,r,m,s){return l(),a("div",null,e[0]||(e[0]=[i('<h1 id="mac-配置和使用总结" tabindex="-1">Mac 配置和使用总结 <a class="header-anchor" href="#mac-配置和使用总结" aria-label="Permalink to &quot;Mac 配置和使用总结&quot;">​</a></h1><h2 id="mac-homebrew-安装问题" tabindex="-1">Mac Homebrew 安装问题 <a class="header-anchor" href="#mac-homebrew-安装问题" aria-label="Permalink to &quot;Mac Homebrew 安装问题&quot;">​</a></h2><p>Homebrew 是 Mac 上的包管理器，可以用来安装各种软件。</p><p>但是有时候在安装过程中会出现网络问题，导致安装失败。我们可以更换镜像源来解决这个问题。</p><p><strong>国内安装加速</strong></p><ul><li><code>/bin/bash -c &quot;$(curl -fsSL https://cdn.jsdelivr.net/gh/ineo6/homebrew-install/install.sh)&quot;</code></li></ul><p><strong>国内卸载加速</strong></p><ul><li><code>/bin/bash -c &quot;$(curl -fsSL https://cdn.jsdelivr.net/gh/ineo6/homebrew-install/uninstall.sh)&quot;</code></li></ul><h2 id="使用-pip-安装第三方包问题" tabindex="-1">使用 <code>pip</code> 安装第三方包问题 <a class="header-anchor" href="#使用-pip-安装第三方包问题" aria-label="Permalink to &quot;使用 `pip` 安装第三方包问题&quot;">​</a></h2><ul><li><a href="https://cn.linux-terminal.com/?p=7961" target="_blank" rel="noreferrer">参考文章</a></li></ul><p>使用参考文章中的第二种方式，将规则写入配置文件后强制安装解决问题了</p><h2 id="mac-快捷键" tabindex="-1">Mac 快捷键 <a class="header-anchor" href="#mac-快捷键" aria-label="Permalink to &quot;Mac 快捷键&quot;">​</a></h2><ul><li><code>Command + R</code> 浏览器刷新</li><li><code>Command + W</code> 关闭当前窗口</li><li><code>Command + Q</code> 退出程序</li><li><code>Command + Tab</code> 切换应用</li><li><code>Command + Shift + 3</code> 截屏</li><li><code>Command + Shift + 4</code> 截屏</li><li><code>Command + Option + Esc</code> 强制退出程序</li><li><code>Command + Option + V</code> 粘贴并替换</li></ul><h2 id="vscode-快捷键" tabindex="-1">VSCode 快捷键 <a class="header-anchor" href="#vscode-快捷键" aria-label="Permalink to &quot;VSCode 快捷键&quot;">​</a></h2><ul><li><code>Command + Shift + P</code> + <code>F1</code> 打开命令面板</li><li><code>Control + ~</code> 打开终端</li><li><code>Command + W</code> 关闭当前窗口</li><li><code>Command + P</code> 快速打开文件</li><li><code>Command + Shift + N</code> 打开新窗口</li><li><code>Command + Shift + W</code> 关闭窗口</li><li><code>Command + Shift + F</code> 全局搜索</li><li><code>Command + Shift + G</code> 全局搜索</li><li><code>Command + Shift + H</code> 全局替换</li></ul>',15)]))}const u=o(c,[["render",d]]);export{p as __pageData,u as default};
